mapper.select:
SELECT p.partNumber, p.partName, p.partNo, p.partPrice, m.modelName FROM PART p LEFT OUTER JOIN MODEL m ON p.modelNo = m.modelNo
WHERE (p.partName LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY m.modelNo DESC LIMIT #{skip}, #{perPageNum}

mapper.update:
UPDATE PART
SET partName = #{partName}, partNo = #{partNo}, partPrice = #{partPrice}
WHERE partNumber = #{partNumber}
